import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [context, setContext] = useState([]); // Store conversation context

  const handleSend = async () => {
    if (input.trim()) {
      const userMessage = { text: input, sender: 'user' };
      setMessages((prev) => [...prev, userMessage]);
      setContext((prev) => [...prev, { role: 'user', content: input }]);
      setInput('');

      try {
        const response = await fetch('http://localhost:5000/chat', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ message: input, context }),
        });
        const data = await response.json();
        if (data.reply) {
          const botMessage = { text: data.reply, sender: 'bot' };
          setMessages((prev) => [...prev, botMessage]);
          setContext((prev) => [...prev, { role: 'assistant', content: data.reply }]);
          speakResponse(data.reply);
        } else if (data.error) {
          setMessages((prev) => [...prev, { text: `Error: ${data.error}`, sender: 'bot' }]);
        }
      } catch (error) {
        setMessages((prev) => [...prev, { text: 'Error: Failed to connect to server.', sender: 'bot' }]);
      }
    }
  };

  const startListening = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = 'en-US';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onresult = (event) => {
      const speechResult = event.results[0][0].transcript;
      setInput(speechResult);
      setIsListening(false);
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
      setMessages((prev) => [...prev, { text: 'Error: Speech recognition failed.', sender: 'bot' }]);
      setIsListening(false);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.start();
  };

  const speakResponse = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    const voices = window.speechSynthesis.getVoices();
    const femaleVoice = voices.find(voice => voice.name.includes('Female') || voice.gender === 'female') || voices[0];
    utterance.voice = femaleVoice;
    utterance.pitch = 1.2;
    utterance.rate = 1;
    window.speechSynthesis.speak(utterance);
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleFileUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await fetch('http://localhost:5000/upload', {
          method: 'POST',
          body: formData,
        });
        const data = await response.json();
        setMessages((prev) => [...prev, { text: data.message || 'File uploaded successfully.', sender: 'bot' }]);
      } catch (error) {
        setMessages((prev) => [...prev, { text: 'Error: Failed to upload file.', sender: 'bot' }]);
      }
    }
  };

  useEffect(() => {
    const chatWindow = document.querySelector('.chat-window');
    chatWindow.scrollTop = chatWindow.scrollHeight;
    window.speechSynthesis.onvoiceschanged = () => {
      const voices = window.speechSynthesis.getVoices();
      console.log('Available voices:', voices);
    };
  }, [messages]);

  return (
    <div className={`min-h-screen flex font-inter ${isDarkMode ? 'bg-gradient-to-br from-gray-900 to-black text-white' : 'bg-am-gray text-gray-900'}`}>
      {/* Sidebar */}
      <div className="w-64 bg-gray-900/95 p-5 flex flex-col shadow-2xl border-r border-gray-800/60">
        <div className="flex items-center mb-6">
          <div className="w-10 h-10 bg-am-blue text-white flex items-center justify-center rounded-full font-bold">AM</div>
          <h1 className="text-xl font-semibold ml-3 text-am-blue">Abdul Moiz's AI</h1>
        </div>
        <button className="bg-am-blue hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center mb-6 transition duration-300">
          <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
            <path d="M10 2a8 8 0 100 16 8 8 0 000-16zM1 10a9 9 0 1118 0 9 9 0 01-18 0zm10-7a1 1 0 00-1 1v4a1 1 0 001 1h4a1 1 0 100-2h-3V4a1 1 0 00-1-1z"/>
          </svg>
          New Chat
        </button>
        <div className="flex-1 overflow-y-auto space-y-2">
          <p className="text-gray-500 text-sm mb-3">Recent Chats</p>
          <ul>
            <li className="p-2 text-gray-400 hover:bg-gray-800/70 rounded-lg cursor-pointer transition duration-300">Chat 1</li>
          </ul>
        </div>
        <button
          className="bg-gray-700 hover:bg-gray-600 text-white px-4 py-2 rounded-lg mt-6 transition duration-300"
          onClick={() => setIsDarkMode(!isDarkMode)}
        >
          {isDarkMode ? 'Light' : 'Dark'}
        </button>
      </div>

      {/* Chat Window */}
      <div className="flex-1 flex flex-col">
        <div className="bg-am-blue/10 p-4 border-b border-gray-200/20 flex justify-between items-center">
          <div>
            <h2 className="text-xl font-semibold text-am-blue">Abdul Moiz's Voice AI</h2>
            <p className="text-sm text-gray-600 dark:text-gray-400">Powered by advanced AI</p>
          </div>
        </div>
        <div className="flex-1 p-6 overflow-y-auto space-y-4 chat-window">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`p-3 rounded-xl max-w-[70%] shadow-lg animate-fade-in ${
                msg.sender === 'user'
                  ? 'bg-am-blue text-white ml-auto'
                  : 'bg-gray-100 text-gray-900 dark:bg-gray-800 dark:text-white'
              }`}
            >
              {msg.text}
            </div>
          ))}
        </div>
        <div className="p-4 border-t bg-white dark:bg-gray-900/90">
          <div className="flex items-center space-x-3 max-w-3xl mx-auto">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSend()}
              className={`flex-1 p-3 rounded-lg border focus:outline-none focus:ring-2 focus:ring-am-blue ${
                isDarkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white text-gray-900 border-gray-300'
              } placeholder-gray-500`}
              placeholder="Ask Abdul Moiz's AI..."
            />
            <button
              onClick={handleSend}
              className="bg-am-blue hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition duration-300"
            >
              Send
            </button>
            <button
              onClick={startListening}
              className={`bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg transition duration-300 ${
                isListening ? 'animate-pulse' : ''
              }`}
            >
              🎙️
            </button>
            <input
              type="file"
              onChange={handleFileChange}
              className="text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
              accept="image/*,.pdf"
            />
            <button
              onClick={handleFileUpload}
              className="bg-am-blue hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition duration-300"
            >
              Upload
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
